ALTER DEFAULT PRIVILEGES FOR USER the_user IN SCHEMA the_schema GRANT SELECT, INSERT, DELETE, UPDATE ON TABLES TO the_other_user;

ALTER DEFAULT PRIVILEGES FOR USER the_user IN SCHEMA the_schema GRANT EXECUTE ON FUNCTIONS TO the_other_user;

ALTER DEFAULT PRIVILEGES FOR USER the_user IN SCHEMA the_schema GRANT EXECUTE ON PROCEDURES TO the_other_user;

ALTER DEFAULT PRIVILEGES FOR USER the_user IN SCHEMA the_schema GRANT EXECUTE ON PROCEDURE the_procedure(f1 INT) TO the_other_user;

ALTER DEFAULT PRIVILEGES FOR USER the_user IN SCHEMA the_schema GRANT EXECUTE ON PROCEDURE the_procedure(f1 INT, f2 BOOLEAN) TO the_other_user;

ALTER DEFAULT PRIVILEGES FOR USER the_user IN SCHEMA the_schema GRANT EXECUTE ON PROCEDURE the_procedure(INT) TO the_other_user;

